	inAmt := int64(5003)
	toAddr := "bchtest:qp5vev8yjxzyf0wmqhwvkvfa3jtear397gwsfxg7sa"

	_, sigHash, err := c.GetRedeemByUserTxSigHash(txid, vout, inAmt, toAddr)
